pre, .code {background: #f7f7f7;border-left: 5px solid #ccc;font: 1em/1.5 "Courier News", monospace;margin: 5px 0 15px;padding: 10px 15px !important;}blockquote {padding: 1em 40px 1em 15px;}blockquote span.open {background: url(../images/so-q.gif) no-repeat left top;padding: 0 0 0 15px;}blockquote span.close {background: url(../images/sc-q.gif) no-repeat bottom right;padding: 0 15px 0 0;}blockquote.testimonial {padding: 0 0 10px;border-bottom: 1px solid #E7E8E6;}blockquote.testimonial span.author {background: url(../images/icon-author.gif) no-repeat 5px center;display: block;font-weight: bold;margin-top: 5px;padding-left: 20px;}.dropcap {color: #333;display: block;float: left;font: 60px/40px Georgia, Times, serif;padding: 7px 8px 0 0;}p.error {background: url(../images/icon-error.gif) no-repeat top left;color: #CC0000;padding-left: 25px;}p.message {background: url(../images/icon-info.gif) no-repeat top left;color: #2C79B3;padding-left: 25px;}p.tips {background: url(../images/icon-tips.gif) no-repeat top left;color: #E58C00;padding-left: 25px;}.highlight {background: #ffffdd;font-weight: bold;padding: 1px 5px;}p.stickynote {background: url(../images/icon-sticky.png) no-repeat 10px center #fcfcfc;border: 1px solid #ddd;padding: 10px 0px 10px 50px;}p.download {background: url(../images/icon-download.png) no-repeat 10px center #fcfcfc;border: 1px solid #ddd;padding: 15px 0px 15px 50px;}.blocknumber {clear: both;padding: 5px 15px 10px;position: relative;}ul.checklist {list-style: none;}ul.checklist li {background: url(../images/icon-checklist.gif) no-repeat 0 0px;margin-left: 15px;padding: 0 0 0 20px;}ul.star {list-style: none;}ul.star li {background: url(../images/icon-star.gif) no-repeat 0 0px;margin-left: 15px;padding: 0 0 0 20px;}ul.arrow {list-style: none;}ul.arrow li {background: url(../images/icon-arrow.gif) no-repeat 3px 3px;margin-left: 15px;padding: 0 0 0 15px;}ul.bookmark-links li {background: url(../images/arrow.png) no-repeat 0px 10px !important;border-botto