;}div.bubble3 div div div {background: url(../images/bub3-tr.gif) no-repeat top right;}div.bubble3 div div div div {background: url(../images/bub3-tl.gif) no-repeat top left;}div.bubble3 div div div div div {margin: 0;padding: 10px 15px 45px;width: auto;}div.bubble4 div {background: url(../images/bub4-br.gif) no-repeat bottom right;margin: 0;width: 100%;}div.bubble4 div div {background: url(../images/bub4-bl.gif) no-repeat bottom left;}div.bubble4 div div div {background: url(../images/bub4-tr.gif) no-repeat top right;}div.bubble4 div div div div {background: url(../images/bub4-tl.gif) no-repeat top left;}div.bubble4 div div div div div {margin: 0;padding: 10px 15px 45px;width: auto;}.box-grey,.box-hilite {background-color: #f7f7f7;border: 3px solid #ddd;margin: 20px 0 0 0;padding: 10px !important;}.box-hilite {border: 3px solid #d6e9f3;background: #fff;}.legend-title {background: #FFFFFF;display: block;float: left;font-weight: bold;left: 7px;margin: 0 !important;padding: 0 5px !important;position: absolute;top: -15px;}.legend {border: 3px solid #CCCCCC;margin: 20px 0;padding: 0 10px;position: relative;}.legend-hilite .legend-title {color: #006699;}.legend-hilite {border: 3px solid #d6e9f3;margin: 20px 0;padding: 0 10px;position: relative;}div.ja-adv {border: 1px solid #ddd;padding: 10px;}div.ja-adv span.sponsor {color: #999;display: block;font-size: 92%;text-align: right;}.highslide-header {border-bottom: 1px solid #CCCCCC;}.highslide-header li {background: none;}.highslide-body {padding: 10px 0 !important;}.highslide {margin: 0;}.highslide img {padding: 2px;border: 1px solid #CCCCCC;}.highslide:hover img {border: 1px solid #ABABAB !important;}div.marketContainer {width: 25%;float: left;padding: 0 10px;border-right: 1px solid #CCCCCC;font-size: 92%;color: #666666;}div.marketLabel {text-transform: uppercase;font-weight: bold;}.negLast, .negChange, .negChangePct {color: #CC0000;}.negLast {color: #FFFFFF;background: #006699;font-weight: bold;padding: 1px 5px;}.negUpdate {font-size: 92%;color: #666666;clear: both;padding: 5px 0;}div.discussion-wrap {background: url(../images/dis-arrow.gif) no-repeat left bottom;padding-bottom: 15px;}div.discussion-content {border: 5px solid #D6E9F3;border-bottom: none;padding: 5px;}div.discussion-content h4 {margin: 0 0 10px !important;}div.discussion-content img {margin-right: 15px;}span.author {display: block;clear: both;}div.ja-feature {border-bottom: 1px dotted #ddd;padding-bottom: 10px;margin-bottom: 20px;}div.ja-feature hr{border: none;border-bottom: 1px dotted #DDDDDD;clear: both;}div.ja-feature ul {padding-left: 20px;margin: 20px 0;}div.ja-feature li {background: url(../images/feature-arrow.gif) no-repeat left top;padding-left: 30px;line-height: 21px;}div.ja-feature li p {color: #797979;font-size: 88%;}span.feature-number {background: #B33333;color: #FFFFFF;padding: 2px 6px;margin-right: 5px;}h4.feature-title {color: #B33333;font-size: 150%;}